mapper.select:
		select 


		from resource where 1=1 


        	and status = #{status, jdbcType=INTEGER}



        	and resource_id = #{resourceId, jdbcType=VARCHAR}



        	and md5 = #{md5, jdbcType=VARCHAR}



        	and file_name = #{fileName, jdbcType=VARCHAR}


      	order by id

